INSERT INTO `ykienkhachhang` (`id_yk`, `created_at`, `updated_at`, `ho_ten`, `chuc_vu`, `noi_dung`, `anh_dd`) VALUES
(4, '2018-07-02 20:53:09', '2018-10-11 00:45:40', 'Nguyên thị thùy trang', 'Trưởng phòng công ty đầu tư AGP', 'Doanh nghiệp chúng tôi có đã có nhiều năm hợp tác với thẩm định giá VNG Việt Nam. Tôi đánh giá cao năng lực tư vấn, hiệu quả công việc và tinh thần sẵn sàng hỗ trợ của Quý công ty.', '1539243940testimonial-2-350x250.jpg'),
(5, '2018-07-02 20:53:25', '2018-10-11 00:45:46', 'Nguyễn Tiến Mạnh', 'Trưởng phòng Cty CP Dịch vụ Thương mại Tường Minh', 'Đội ngũ cán bộ, chuyên viên Thẩm định giá VNG Việt Nam nhiệt tình, có kinh nghiệm và trình độ chuyên môn nghiệp vụ cao. Hy vọng sự hợp tác giữa Quý công ty và đơn vị chúng tôi bền vững, đem lại lợi ích thiết thực cho cả hai bên.', '1539243946testimonial-4-350x250.jpg'),
(6, '2018-07-03 02:04:02', '2018-10-11 00:46:11', 'Phan Tất Thắng', 'Giám đốcc Cty TNHH Tư vấn & Đầu tư Hoàng An', 'Hợp tác với Thẩm định giá VNG Việt Nam từ những năm Quý công ty mới thành lập, tôi thực sự thấy hài lòng về chất lượng dịch vụ, sự nhiệt tình, và chuyên nghiệp của đội ngũ chuyên viên.', '1539243971testimonial-3-350x250.jpg'),
(7, '2018-07-10 21:22:15', '2018-10-11 00:45:23', 'Trịnh Minh Hiền', 'Phó giám đốc Công ty CP Phú Tài', 'Là đối tác của Thẩm định giá VNG Việt Nam từ năm 2015, tuy nhiên chỉ qua một vài dự án hợp tác, chúng tôi thấy được hiệu quả công việc cao, phong cách làm việc chuyên nghiệp. Mong rằng doanh nghiệp tôi sẽ trở thành đối tác lâu dài với Thẩm định giá VNG', '1539243923testimonial-1-350x250.jpg');

